import classes from "../css/workerLP.module.css";
import showlogo from "../media/showlogo.png";
import { Form,Button,Card, Alert} from 'react-bootstrap';
import { Link, useHistory } from "react-router-dom";
import React, { useRef, useState } from "react";
import { useAuth } from "../context/AuthContext";

function WorkerLoginPage() {

  const emailRef = useRef();
  const passwordRef = useRef();
  const { login } = useAuth();
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const history = useHistory();
  const [show, setShow] = React.useState(false);

  async function handleSubmit(e) {
    e.preventDefault();

    try {
      setError("");
      setLoading(true);
      await login(emailRef.current.value, passwordRef.current.value);
      history.push("/WorkerStartPage");
    } catch {
      setError("Failed to sign in");
    }
    setLoading(false);
  }

  return (
    <div className={classes.containerPage}>
      <span className={classes.titlePrijava}>Prijava za djelatnike </span>

      <div className={classes.circle1}></div>
      <div className={classes.circle2}></div>
      <div className={classes.circle3}></div>

      <div className={classes.name}></div>
      <div className={classes.name}></div>

      {error & <Alert variant="danger">{error}</Alert>}
      <Form onSubmit={handleSubmit}>

        <Form.Group id="email">
            <Form.Control type="email" ref={emailRef} requred className={classes.titleKorisnickoime} placeholder="KorisniÄko ime"/>
        </Form.Group>
        <div className={classes.underline}></div>

        <Form.Group id="password">
            <Form.Control type={show ? "text" : "password"} ref={emailRef} requred className={classes.titleKorisnickoime} autoComplete={false} placeholder="Lozinka" />
        </Form.Group>
        <div className={classes.underline2}></div>

        <div className={classes.containerPW}>
            <img src={showlogo} alt="show password" onClick={() => setShow(!show)}></img>
        </div>
        <div className={classes.btnLogindiv}>
          <Button disabeled={loading} className={classes.loginbtn} type="submit">NASTAVI</Button> 
        </div>    

    </Form>
      <div className={classes.containerbottom}>
        <div className={classes.recoverydiv}>
          <span className={classes.txtForgot}>Zaboravljena </span>
          <span className={classes.txtRecover}>lozinka?</span>
        </div>
      </div>
    </div>
  );
}

export default WorkerLoginPage;
